import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [],
  totalPrice: 0,
};

export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    // addItem(state, action) {
    //   state.items.push(action.payload);
    //   state.totalPrice = state.items.reduce((sum, obj) => {
    //     return obj.price + sum;
    //   }, 0);
    // },
    addItem(state, action) {
      const itemFind = state.items.find((obj) => obj.id === action.payload.id);

      if (itemFind) {
        itemFind.count++;
      } else {
      }
    },
    itemRemove(state, active) {
      state.items = state.items.filter((obj) => obj.id !== active.payload);
    },
    itemClear(state, active) {
      state.items = [];
    },
  },
});

export const { addItem, itemRemove, itemClear } = cartSlice.actions;

export default cartSlice.reducer;
