import axios from "axios";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const fetchPizzas = createAsyncThunk("pizza/fetchPizzasStatus", async () => {
  const res = await axios.get(
    `https://639c95a242e3ad6927364e55.mockapi.io/items?${
      activeIndex > 0 ? `category=${activeIndex}` : ""
    }&sortBy=${activeSort.sortProperty}&order=desc${
      searchValue ? `&search=${searchValue}` : ""
    }`
  );
  return res.data;
});

const initialState = {
  items: [],
};

export const pizzaSlice = createSlice({
  name: "pizza",
  initialState,
  reducers: {
    setItems(state, active) {
      state.items = active.payload;
    },
  },
});

export const { setItems } = pizzaSlice.actions;

export default pizzaSlice.reducer;
